mapper.select:
select

from agent_pay_log
where
  with_draw_task_id = #{withDrawTaskId,jdbcType=BIGINT}

    and status = #{status,jdbcType=INTEGER}

  order by id desc

mapper.update:
update agent_pay_log
set
`status` = #{toStatus,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT} and status = #{fromStatus,jdbcType=INTEGER}
